document.addEventListener("DOMContentLoaded",function(){window.innerWidth<=767&&(document.querySelectorAll("nav.menu .menu__item--parent > .menu__link").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),this.closest(".menu__item--parent").classList.toggle("menu__item--expanded")})}),document.addEventListener("click",function(e){e.target.closest("nav.menu .menu__item--parent")||document.querySelectorAll("nav.menu .menu__item--parent").forEach(e=>e.classList.remove("menu__item--expanded"))}))})