document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("nav.menu"),t=document.querySelector(".menu-toggle");e&&t&&t.addEventListener("click",function(t){t.preventDefault(),e.classList.toggle("menu--open")}),document.querySelectorAll("nav.menu .menu__item--parent > .menu__link > .menu__arrow").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),e.stopPropagation(),this.closest(".menu__item--parent").classList.toggle("menu__item--expanded")})})})