document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("nav.menu"),t=document.querySelector(".menu-toggle")||document.querySelector(".js-menu-toggle");e&&t&&t.addEventListener("click",t=>{t.preventDefault(),e.classList.toggle("menu--open")}),e.querySelectorAll(".menu__item--parent > .menu__link > .menu__arrow").forEach(e=>{e.addEventListener("click",t=>{t.preventDefault(),t.stopPropagation(),e.closest(".menu__item--parent").classList.toggle("menu__item--expanded")})})})